module.exports = {
  /**
   * insert post into database
   * @param {object} request request from API
   * @param {object} response response from AP
   * @param {object} next next request or middleware
   */
  adiciona: async (req, res, next) => {
    try {
      const user = await req.user;
      req.body.autor = user.id;
      const post = new Post(req.body);
      await post.adiciona();

      res.status(201).send(post);
    } catch (error) {
      return next(error);
    }
  },

  lista: async (req, res, next) => {
    try {
      const user = await req.user;
      const posts = await Post.lista(user);
      res.send(posts);
    } catch (error) {
      return next(error);
    }
  },

  deleta: async (req, res, next) => {
    const post = await Post.buscaPorId(req.params.id);

    if (!post) {
      return next(new NotFoundError());
    }

    try {
      await post.deleta();
      res.status(200).send();
    } catch (error) {
      return next(error);
    }
  },
};

const db = require("../../database");
const ac = require("../usuarios/regras-acesso");

module.exports = {
  adiciona: (post) => {
    return new Promise((resolve, reject) => {
      db.run(
        `
        INSERT INTO posts (
          titulo, 
          conteudo,
          autor
        ) VALUES (?, ?, ?)
      `,
        [post.titulo, post.conteudo, post.autor],
        (erro) => {
          if (erro) {
            return reject("Erro ao adicionar o post!");
          }

          return resolve();
        }
      );
    });
  },

  buscaPorId: (id) => {
    return new Promise((resolve, reject) => {
      db.get(
        `
          SELECT *
          FROM posts
          WHERE id = ?
        `,
        [id],
        (erro, post) => {
          if (erro) {
            return reject("Não foi possível encontrar o post!");
          }

          return resolve(post);
        }
      );
    });
  },

  deleta: (post) => {
    return new Promise((resolve, reject) => {
      db.run(
        `
          DELETE FROM posts
          WHERE id = ?
        `,
        [post.id],
        (erro) => {
          if (erro) {
            return reject("Erro ao deletar o post");
          }
          return resolve();
        }
      );
    });
  },

  lista: (user) => {
    return new Promise((resolve, reject) => {
      const permission = ac.can(user.cargo).readAny("post");
      const attributes = permission.attributes.join(",");

      const queryUser = `SELECT ${attributes} FROM posts`;

      db.all(queryUser, (erro, resultados) => {
        if (erro) {
          return reject("Erro ao listar os posts!");
        }

        return resolve(resultados);
      });
    });
  },
};

const postsDao = require("./posts-dao");
const validacoes = require("../validacoes-comuns");

/**
 * represents Post Model
 */
class Post {
  /**
   * initialize post model
   * @param {object} post
   */
  constructor(post) {
    this.id = post.id;
    this.titulo = post.titulo;
    this.conteudo = post.conteudo;
    this.autor = post.autor;
    this.valida();
  }

  /**
   * inserts post model
   */
  adiciona() {
    return postsDao.adiciona(this);
  }

  /**
   * validate post model, according rules
   */
  valida() {
    validacoes.campoStringNaoNulo(this.titulo, "título");
    validacoes.campoTamanhoMinimo(this.titulo, "título", 5);

    validacoes.campoStringNaoNulo(this.conteudo, "conteúdo");
    validacoes.campoTamanhoMaximo(this.conteudo, "conteúdo", 140);
  }

  /**
   * lists posts according user
   * @param {User} user
   * @return {Array} posts
   */
  static lista(user) {
    return postsDao.lista(user);
  }

  /**
   * deletes post model
   */
  async deleta() {
    return postsDao.deleta(this);
  }

  /**
   * searchs post model
   * @param {Integer} id
   * @return {object} post
   */
  static async buscaPorId(id) {
    const post = await postsDao.buscaPorId(id);
    if (!post) {
      return null;
    }

    return new Post(post);
  }
}

module.exports = Post;

